using namespace std;
typedef pair<long double, long double> p;
#define X first
#define Y second

p transition(p p0, p p1, p p2){
  p t;
  double theta = atan2(p1.Y-p0.Y,p1.X-p0.X);
  t.X = (cos(theta)*(p2.X-p0.X)+sin(theta)*(p2.Y-p0.Y))*cos(theta)+p0.X;
  t.Y = (cos(theta)*(p2.X-p0.X)+sin(theta)*(p2.Y-p0.Y))*sin(theta)+p0.Y;
  return t;
}

