}

// Actual Solver
inline void solve()
{
    cout << fixed << setprecision(10);
    Line l;
    l.read();
    int q;
    cin >> q;
    while (q--)
    {
        Point p;
        p.read();
        auto ans = l.proj(p);
        cout << ans.x << ' ' << ans.y << '\n';
    }
}

