struct vecter {
	double x, y, r;
	vecter() {}
	vecter(double x_, double y_) :x(x_), y(y_) {
		r = sqrt(x*x + y * y);
	}
	vecter operator+(vecter v) {
		return vecter(x + v.x, y + v.y);
	}
	vecter operator-(vecter v) {
		return vecter(x - v.x, y - v.y);
	}
	vecter operator/(double k) {
		return vecter(x/k, y/k);
	}
	vecter operator*(double k) {
		return vecter(x*k,y*k);
	}
	double operator*(vecter v) { //内積
		return x * v.x + y * v.y;
	}
	double angle(vecter v) { //なす角(Radians)
		if((r * v.r)>0)
		return acos(*this*v / (r * v.r));
		else return 0;
	}
	vecter unit() {
		return (r == 0 ? vecter() : vecter(x, y) / r);
	}
};
vecter dir(double rad) {
	return vecter(cos(rad),sin(rad));
}
