// 多角形
//   辺をメンバ変数にしない→メモリ節約・edge(void)がO(頂点数)。edge().begin()を使っている箇所は書き換える必要がある
#include<vector>
#include<algorithm>
#include<numeric>
#include<utility>
class Polygon {
public:
  // 編集
  void add(const Point& p) {
    if(!vertex_.empty()) {
      if(!edge_.empty()) edge_.pop_back();
      edge_.push_back(Segment(vertex_.back(), p));
      edge_.push_back(Segment(p, vertex_.front()));
    }
    vertex_.push_back(p);
  }
  void clear() {
    vertex_.clear();
    edge_.clear();
  };
  // 情報
  int size() const {return vertex_.size();}
  Point vertex(const int n) const {return vertex_.at(n);}
  Segment edge(const int n) const {return edge_.at(n);}
  const std::vector<Point>& vertex() const {return vertex_;}
  const std::vector<Segment>& edge() const {return edge_;}
  // 道具
  bool touch(const Point& p) const {
    return std::any_of(edge().begin(), edge().end(), [=](const Segment& s) {return ccw(s.initial, s.terminal, p) == ON_SEGMENT;});
  }
  bool intersect(const Point& p) const {
    bool in = false;                    // p=(x,y)から(INF,y)への半直線と多角形とが交差する回数 mod 2
    for(const auto& e: edge()) {
      if(::intersect(p, e)) return true;
      Point a = e.initial  - p;
      Point b = e.terminal - p;
      if(a.y > b.y) std::swap(a, b);
      if(a.y <= 0.0 && 0.0 < b.y && cross(a, b) < EPS) in = !in;                // 誤差怖い
    }
    return in;
  }
  double area() const {
    return std::abs(std::accumulate(edge().begin(), edge().end(), 0, [=](const int sum, const Segment& e){return sum + cross(e.initial, e.terminal);})) / 2.0;
  }
  bool convex() const {
    bool positive = false;
    bool negative = false;
    for(int i = 0; i < size(); ++i) {
      auto position = ccw(vertex(i), vertex((i + 1) % size()), vertex((i + 2) % size()));
      positive |= position == CLOCKWISE;
      negative |= position == COUNTER_CLOCKWISE;
      if(negative && positive) return false;
    }
    return true;
  }
private:
  std::vector<Point> vertex_;
  std::vector<Segment> edge_;
};
