{
	cout << fixed << setprecision(20);

	Line l;
	Point p;
	int q;

	cin >> l.p1.x >> l.p1.y >> l.p2.x >> l.p2.y >> q;
	while (q--){
		cin >> p.x >> p.y;

		Point pr = project(l, p);
		cout << pr.x << " " << pr.y << endl;
	}
}

