	point = Point(x, y);
	return is;
}

class Line {
public:
  Point a, b;

  Line() {}

  Line (const Point& a, const Point& b) : a(a), b(b) {}

  Point vec() const {
    return b - a;
  }
};

ostream& operator<<(ostream& os, const Line& line) {
