    cin>>q;
    vector<Point> pp(q);
    for(int i=0; i<q; i++){
        cin>>pp[i].x>>pp[i].y;
    }
    double rc1,rc2,b1;
    rc1=(p1.y-p2.y)/(p1.x-p2.x);
    if(p1.x-p2.x==0){
        b1=p1.y;
        rc2=0;
    }else{
        b1=p1.y-rc1*p1.x;
        rc2=(-1)/rc1;
    }
    vector<double> bb(q);
    vector<Point> ans(q);
    for(int i=0; i<pp.size(); i++){
        if(rc1==0){
            ans[i].x=pp[i].x;
