            bb[i]=pp[i].y-rc2*pp[i].x;
            if(p1.x-p2.x==0){
                ans[i].x=0;
            }else if(rc2-rc1==0){
                ans[i].x=0;
            }else{
                ans[i].x=(b1-bb[i])/(rc2-rc1);
            }
        }
        if(p1.x-p2.x==0){
            ans[i].y=bb[i];
        }else{
            ans[i].y=rc1*ans[i].x+b1;
        }
