    cin >> x1 >> y1 >> x2 >> y2;
    line l = make_line({x1, y1}, {x2, y2});

    int q;
    cin >> q;
    while (q--) {
        double x, y;
        cin >> x >> y;
        printvec2(foot({x, y}, l), 10);
    }
}

