    scanf("%lf %lf %lf %lf", &x0, &y0, &x1, &y1);
    scanf("%d", &q);
    a = Point(x0, y0);
    b = Point(x1, y1);

    for(int i = 0; i < q; i++) {
        scanf("%lf %lf", &x2, &y2);
        c = Point(x2, y2);
        vec v1 = toVec(a, b), v2 = toVec(a, c);

        double costheta = (dot(v1, v2) / (norm(v1) * norm(v2)));
        double lenv2 = norm(v2);
        double lenv1 = norm(v1);
        double proj = lenv2 * costheta;
        double x = (proj / lenv1) * v1.x;
        double y = (proj / lenv1) * v1.y;
        printf("%0.9lf %0.9lf\n", handleNegZero(x), handleNegZero(y));
    }

    return 0;
}

