int sign(ld x,ld eps=global_eps){return (x>=eps)-(x<=-eps);}
int cmp(ld a,ld b,ld eps=global_eps){return sign(a-b,eps);}
struct Point
{
    ld x,y;
    Point():x(0),y(0){}
    Point(ld _x,ld _y):x(_x),y(_y){}
    Point operator +(const Point &p) const
    {return Point(x+p.x,y+p.y);}
    Point operator -(const Point &p) const
    {return Point(x-p.x,y-p.y);}
    ld operator *(const Point &p) const
    {return x*p.x+y*p.y;}
    ld operator %(const Point &p) const
    {return x*p.y-y*p.x;}   
    bool operator <(const Point &p) const
    {return tie(y,x) < tie(p.y,p.x);}
    bool operator ==(const Point &p) const
    {return tie(y,x) == tie(p.y,p.x);}
    Point operator /(const ld &l) const
    {return Point(x/l,y/l);}
    Point operator *(const ld &l) const
    {return Point(x*l,y*l);}
    ld len() const{return hypot(x,y);}
    ld len2() const{return x*x+y*y;}
    ld dis(const Point &a)const{return (*this-a).len();}
    ld dis2(const Point &a)const{return (*this-a).len2();}
    Point unit()const{return *this/len();}
    Point resize(const ld &l)const{return unit()*l;}
};
struct Line
{
    Point p1,p2;
    Line(){}
    Line(Point a,Point b):p1(a),p2(b){}
    Point way() const{return p2-p1;}
    Point norm() const{return {p1.y-p2.y,p2.x-p1.x};}
    bool online(const Point &p) const{return sign((p-p1)%way())==0;}
    bool onsegment(const Point &p)const
    {
        return online(p) && sign((p-p1)*(p2-p))>=0;
    }
    pair<int,Point> intersect(const Line &I) const
    {
        if(sign(I.way()%way())==0) return {online(I.p1)?coin:para,{0,0}};
        const Point &p3=I.p1,&p4=I.p2;
        auto _143=(p1-p4)%(p4-p3),_342=(p3-p4)%(p4-p2);
        return {inte,(p1*_342+p2*_143)/(_143+_342)};
    }
    ld linedistance(const Point &p) const
    {
        return abs((p-p1)%way())/way().len();
    }
    Point project(const Point &p) const
    {
        return intersect(Line(p,p+norm())).second;
    }
    ld segmentdistance(const Point &p) const
    {
        const auto &pro=project(p);
        if(onsegment(pro)) return p.dis(pro);
        return min(p.dis(p1),p.dis(p2));
    }
};
struct Circle
{
    Point o;ld r;
    Circle(){}
    Circle(Point a,ld b):o(a),r(b){assert(r>=0);}
    int intersect(const Point &p) const
    {
        auto dis2=p.dis2(o);//hypot(p.x-o.x,p.y-o.y);
        int res=cmp(dis2,r*r);//XXX eps may vary, ~ 2 * range_of(r) * eps
        if(res==0) return bord;
        else if(res<0) return insi;
        else return outs;
    }
    tuple<int,Point,Point> intersect(const Line &I) const
    {
        static_assert(is_floating_point<decltype(Point().x)>::value,"Point<ld>!");//is double-like

        auto dis=I.linedistance(o);
        int res=cmp(dis,r);
        if(res==0)
        {
            const auto &pro=I.project(o);
            return {bord,pro,pro};
        }
        else if(res==1) return {outs,{0,0},{0,0}};
        else
        {
            const auto &pro=I.project(o);
            const auto d=sqrt(r*r-o.dis2(pro));
            const auto v=I.way().resize(d);
            return {insi,pro+v,pro-v};
        }
    }
    tuple<int,Point,Point> intersect(const Circle &c) const
    {
        {
            int res=cmp(o.dis(c.o),abs(r-c.r));
            if(res<0) return {insi,{0,0},{0,0}};
            else if(res==0)
            {
                const auto &p=get<1>(intersect(Line(o,c.o)));
                return {tang_in,p,p};
            }
        }
        {
            int res=cmp(o.dis(c.o),r+c.r);
            if(res>0) return {outs,{0,0},{0,0}};
            else if(res==0)
            {
                const auto &p=get<1>(intersect(Line(o,c.o)));
                return {tang_out,p,p};
            }
        }
        {
            Point o1=o,o2=c.o;
            auto r1=r,r2=c.r,d=o1.dis(o2);
            Point mid=(o1*(r2*r2-r1*r1+d*d)+o2*(r1*r1-r2*r2+d*d))/(2*d*d);
            Line I(mid,mid+Point(o1.y-o2.y,o2.x-o1.x));
            auto res=intersect(I);
            return {inte,get<1>(res),get<2>(res)};
        }
    }
};
int main()
{
    int a,b,c,d;
    RI(a,b,c,d);
    Line I({a,b},{c,d});
    int q;RI(q);
    while(q--)
    {
