// a-b-c の角度のうち小さい方を返す
Real get_angle(const Point &a, const Point &b, const Point &c)
{
    const Point v(b - a), w(c - b);
    Real alpha = atan2(v.imag(), v.real()), beta = atan2(w.imag(), w.real());
    if (alpha > beta)
        swap(alpha, beta);
    Real theta = (beta - alpha);
    return min(theta, 2 * acos(-1) - theta);
}

