void pv(double ax, double ay, double bx, double by, 
        double px, double py, double ans[]) {
    //*find the projection point ans of p onto a,b *//
    double x1 = bx-ax, x2 = px-ax, y1 = by-ay, y2 = py-ay;
    ans[0] = 1.0 * (x1*x2+y1*y2)/(x1*x1+y1*y1)*x1 + ax;
    ans[1] = 1.0 * (x1*x2+y1*y2)/(x1*x1+y1*y1)*y1 + ay;
}

