		ans.y = Vectormultiplication(vector1.point, vector2.point) / pow(vector1.size, 2) * vector1.point.y;

		printf("%lf %lf\n", ans.x, ans.y);
	}

	return 0;
}

Point VectorAddition(Point p1, Point p2, int sign) {
	Point point;
	switch (sign) {
	case 1:
		point.x = p2.x + p1.x;
		point.y = p2.y + p1.y;
		break;
	case 2:
		point.x = p2.x - p1.x;
		point.y = p2.y - p1.y;
		break;
	}
	return point;
}

double Vectormultiplication(Point p1, Point p2) {
	Point point;
	double i;
	point.x = p1.x * p2.x;
	point.y = p1.y * p2.y;
	i = point.x + point.y;
	return i;
}

double VectorSize(Point p) {
	return sqrt(pow(p.x, 2) + pow(p.y, 2));
}

