Point VectorAddition(Point p1, Point p2, int sign) {
	Point point;
	switch (sign) {
	case 1:
		point.x = p2.x + p1.x;
		point.y = p2.y + p1.y;
		break;
	case 2:
		point.x = p2.x - p1.x;
		point.y = p2.y - p1.y;
		break;
	}
	return point;

}

double Vectormultiplication(Point p1, Point p2) {
	Point point;
	double i;
	point.x = p1.x * p2.x;
	point.y = p1.y * p2.y;
	i = point.x + point.y;
	return i;
}

double VectorSize(Point p) {
	return sqrt(pow(p.x, 2) + pow(p.y, 2));
}

double Range(Point p1, Point p2) {
	return Vectormultiplication(p1, p2) / (VectorSize(p1) * VectorSize(p2));
}

