				Point2D p2 = new Point2D.Double(in.nextDouble(), in.nextDouble());
				Point2D ans = projection(new Line2D.Double(p0, p1), p2);
				System.out.printf("%.9f %.9f",ans.getX(),ans.getY());
			}
		}
	}

}

