class Projection {
	// tx = x2*cos*cos + y2*sin*cos + x0*sin*sin - y0*sin*cos
	// ty = x2*sin*cos + y2*sin*sin - x0*sin*cos + y0*cos*cos
	double cos2, sin2, sincos;
	double txd, tyd;

	public Projection(int x0, int y0, int x1, int y1) {
		double t2 = (x1 - x0) * (x1 - x0) + (y1 - y0) * (y1 - y0);
		cos2 = (x1 - x0) * (x1 - x0) / t2;
		sin2 = (y1 - y0) * (y1 - y0) / t2;
		sincos = (x1 - x0) * (y1 - y0) / t2;
		txd = x0 * sin2 - y0 * sincos;
		tyd = -x0 * sincos + y0 * cos2;
	}

	public void calc(int x2, int y2, PrintWriter pw) {
		double tx = x2 * cos2 + y2 * sincos + txd;
		double ty = x2 * sincos + y2 * sin2 + tyd;
		pw.print(String.valueOf(tx));
		pw.print(' ');
		pw.println(String.valueOf(ty));
	}
}

class UserScanner {
	private final InputStream in = System.in;
	private final byte[] buffer = new byte[1024];
	private int ptr = 0;
	private int buflen = 0;

	private boolean read() throws IOException {
		ptr = 0;
		buflen = in.read(buffer);
		if (buflen <= 0)
			return false;
		else
			return true;
	}

	private byte getByte() throws IOException {
		if (ptr >= buflen)
			read();
		if (isCtlSpace(buffer[ptr])) {
			return -1;
		} else
			return buffer[ptr++];
	}

	private void skipCtlSpace() throws IOException {
		if (ptr >= buflen)
			read();
		if (isCtlSpace(buffer[ptr])) {
			ptr++;
			skipCtlSpace();
		}
	}

	private boolean isCtlSpace(byte b) {
		if (b <= ' ' || b > '~')
			return true;
		else
			return false;
	}

	public void close() throws IOException {
		in.close();
	}

	public String next() throws IOException {
		skipCtlSpace();
		StringBuilder sb = new StringBuilder();
		byte b;
		while ((b = getByte()) != -1) {
			sb.appendCodePoint(b);
		}
		return sb.toString();
	}

	public int nextInt() throws IOException {
		skipCtlSpace();
		int n = 0;
		boolean minus = false;
		byte b;
		while ((b = getByte()) != -1) {
			if (b == '-')
				minus = true;
			else {
				n *= 10;
				n += (b - '0');
			}
		}
		if (minus)
			return n * -1;
		else
			return n;
	}
}

import java.io.IOException;
import java.io.InputStream;
import java.util.NoSuchElementException;

public class Main {
	private static FastScanner sc = new FastScanner();

	public static void main(String[] args) {
		double x1 = sc.nextDouble();
		double y1 = sc.nextDouble();
		double x2 = sc.nextDouble();
		double y2 = sc.nextDouble();

		double a = (y2 - y1) / (x2 - x1);
		double b = y1 - a * x1;

		int q = sc.nextInt();
		StringBuilder ans = new StringBuilder();
		for(int i=0; i<q; i++) {
			double xp = sc.nextDouble();
			double yp = sc.nextDouble();

			double x = 0;
			double y = 0;
			if(y1 == y2) {
				x = xp;
				y = y1;
			} else if(x1 == x2) {
				x = x1;
				y = yp;
			} else {
				x = (yp + xp / a - b) / (a + 1 / a);
				y = yp + xp / a - x / a;
			}

			ans.append(x);
			ans.append(" ");
			ans.append(y);
			ans.append("\n");
		}

		System.out.print(ans);
	}

	static class FastScanner {
        private final InputStream in = System.in;
        private final byte[] buffer = new byte[1024];
        private int ptr = 0;
        private int buflen = 0;
        private boolean hasNextByte() {
            if(ptr < buflen) {
                return true;
            } else {
                ptr = 0;
                try {
                    buflen = in.read(buffer);
                } catch(IOException e) {
                    e.printStackTrace();
                }
                if(buflen <= 0) {
                    return false;
                }
            }
            return true;
        }
        private int readByte() { if (hasNextByte()) return buffer[ptr++]; else return -1;}
        private static boolean isPrintableChar(int c) { return 33 <= c && c <= 126;}
        private void skipUnprintable() { while(hasNextByte() && !isPrintableChar(buffer[ptr])) ptr++;}
        public boolean hasNext() { skipUnprintable(); return hasNextByte();}
        public String next() {
            if (!hasNext()) throw new NoSuchElementException();
            StringBuilder sb = new StringBuilder();
            int b = readByte();
            while(isPrintableChar(b)) {
                sb.appendCodePoint(b);
                b = readByte();
            }
            return sb.toString();
        }
        public long nextLong() {
            return Long.parseLong(next());
        }
        public int nextInt(){
            return Integer.parseInt(next());
        }
        public double nextDouble(){
            return Double.parseDouble(next());
        }
    }
}

public class Main{
  public void run(java.io.InputStream in, java.io.PrintStream out){
    java.util.Scanner sc = new java.util.Scanner(in);
/*answer*/
    double xp1, xp2, yp1, yp2;
    int q;
    double[] xp, yp;
    double ansx, ansy, t;
    int i;

    xp1 = sc.nextDouble();
    yp1 = sc.nextDouble();
    xp2 = sc.nextDouble();
    yp2 = sc.nextDouble();
    q = sc.nextInt();
    xp = new double[q];
    yp = new double[q];

    for(i = 0;i < q;i++){
      xp[i] = sc.nextDouble();
      yp[i] = sc.nextDouble();
    }
    sc.close();
/*  want to solve
ansx = xp1 + t * (xp2 - xp1)
ansy = yp1 + t * (yp2 - yp1)
(xp2 - xp1) * (xp[i] - ansx) + (yp2 - yp1) * (yp[i] - ansy) = 0
  VV
t((xp2-xp1)^2 + (yp2-yp1)^2) = (xp2-xp1)(xp[i]-xp1) + (yp2-yp1)(yp[i]-yp1)
*/

    for(i = 0;i < q;i++){
      t = (xp2 - xp1) * (xp[i] - xp1) + (yp2 - yp1) * (yp[i] - yp1);
      t = t / ((xp2 - xp1) * (xp2 - xp1) + (yp2 - yp1) * (yp2 - yp1));
      ansx = xp1 + t * (xp2 - xp1);
      ansy = yp1 + t * (yp2 - yp1);
      System.out.printf("%.10f %.10f%n", ansx, ansy);
    }

  }
  public static void main(String[] args){
    (new Main()).run(System.in, System.out);
  }
}

