        int q = sc.nextInt();
        Geometric geometric=new Geometric();
        for(int i = 0 ; i < q ; i++){
            int px = sc.nextInt();
            int py = sc.nextInt();
            double[] kummi=geometric.project((double)x1,(double)y1,(double)x2,(double)y2,(double)px,(double)py);
            out.println(kummi[0]+" "+kummi[1]);
        }
        out.flush();
