	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		Segment seg  =new Segment(new Point(sc.nextInt(),sc.nextInt()),new Point(sc.nextInt(),sc.nextInt()));

		int q=sc.nextInt();
