    {
        static public long[] Sarray() { return ReadLine().Trim().Split().Select(long.Parse).ToArray(); }
        static public List<long> Slist() { return ReadLine().Split().Select(long.Parse).ToList(); }


        static void Main(string[] args)
        {
            var p1p2 = Sarray();
            var p1 = new Point(p1p2[0], p1p2[1]);
            var p2 = new Point(p1p2[2], p1p2[3]);
            var Q = Sarray()[0];
            for (var i = 0; i < Q; ++i)
            {
                var pi = Sarray();
                var p = new Point(pi[0], pi[1]);
                Vector b = p2 - p1;
                double r = VecCalc.dot(p - p1, b) / b.norm();
                var ans = p1 + b * r;
                WriteLine($"{ans.X} {ans.Y}");

            }
        }
    }
