        /// <returns></returns>
        public static Point Projection(Point p1, Point p2, Point p)
        {
            Point a = p2 - p1;
            Point b = p - p1;
            double t = Naiseki(a, b) / Naiseki(a, a);
            return t * a + p1 ;
        }
    }
}

