namespace Projection
{
    internal class Program
    {

        public static void Main(string[] args)
        {
            Solve();
        }

        static void Solve()
        {
            var inputs = InputLine();
            var startPos = new Vector2(inputs[0], inputs[1]);
            var endPos = new Vector2(inputs[2], inputs[3]);
            var n = int.Parse(ReadLine());

            for (var i = 0; i < n; i++)
            {
                inputs = InputLine();
                var vertex = GetProjection(startPos, endPos, new Vector2(inputs[0], inputs[1]));
                WriteLine(vertex.x + " " + vertex.y);
            }
        }

        static Vector2 GetProjection(Vector2 startPos, Vector2 endPos, Vector2 objPos)
        {
            var axisX = endPos - startPos;
            var axisY = objPos - startPos;
            var project = (Vector2.Dot(axisX, axisY) / (Vector2.Length(axisX) * Vector2.Length(axisX))) * axisX;
            return project + startPos;
        }

        static IReadOnlyList<double> InputLine()
        {
            var inputs = ReadLine()
                .Split(' ')
                .Select(double.Parse)
                .ToList();

            return inputs;
        }

    }

    public struct Vector2
    {
        public double x, y;

        public Vector2(double xy)
        {
            this.x = this.y = xy;
        }

        public Vector2(double x, double y)
        {
            this.x = x;
            this.y = y;
        }

        public static Vector2 operator +(Vector2 a, Vector2 b)
            => new Vector2(a.x + b.x, a.y + b.y);

        public static Vector2 operator -(Vector2 a, Vector2 b)
            => new Vector2(a.x - b.x, a.y - b.y);


        public static Vector2 operator *(double d, Vector2 vec)
            => new Vector2(d * vec.x, d * vec.y);

        public static double Dot(Vector2 a, Vector2 b)
            => a.x * b.x + a.y * b.y;

        public static double Length(Vector2 a)
            => Math.Sqrt(a.x * a.x + a.y * a.y);
    }

}

