        public static bool DoesIntersect(Point p1, Point p2, Point p3, Point p4)
        {
            if (p1.x >= p2.x)
                if ((p1.x < p3.x && p1.x < p4.x) || (p2.x > p3.x && p2.x > p4.x))
                    return false;
                else if ((p2.x < p3.x && p2.x < p4.x) || (p1.x > p3.x && p1.x > p4.x))
                        return false;
            if (p1.y >= p2.y)
                if ((p1.y < p3.y && p1.y < p4.y) || (p2.y > p3.y && p2.y > p4.y))
                    return false;
                else if ((p2.y < p3.y && p2.y < p4.y) || (p1.y > p3.y && p1.y > p4.y))
                        return false;
            Point a = p1 - p2;
            Point b = p3 - p1;
            Point c = p4 - p1;
            long tc = a.x * b.y + a.y * (-b.x);
            long td = a.x * c.y + a.y * (-c.x);
            Point d = p3 - p4;
            Point e = p1 - p3;
            Point f = p2 - p3;
            long ta = d.x * e.y + d.y * (-e.x);
            long tb = d.x * f.y + d.y * (-f.x);
            if (tc * td < 0 && ta * tb < 0) return true;
            return false;
        }
    }
