var scanner = bufio.NewScanner(os.Stdin)

func nextString() string {
	scanner.Scan()
	return scanner.Text()
}

func nextInt() int {
	n, err := strconv.Atoi(nextString())
	if err != nil {
		fmt.Printf("strconv.Atoi failed: %v\n", err)
	}
	return n
}

func nextFloat64() (float64, error) {
	return strconv.ParseFloat(nextString(), 64)
}

func norm2(x, y float64) float64 {
	return x*x + y*y
}

func main() {
	scanner.Split(bufio.ScanWords)

	p1x, _ := nextFloat64()
	p1y, _ := nextFloat64()
	p2x, _ := nextFloat64()
	p2y, _ := nextFloat64()

	intercept := 0.0
	if p1x == p2x {
		intercept = 0
	} else {
		intercept = (p2x*p1y - p1x*p2y) / (p2x - p1x)
	}
	p1y -= intercept
	p2y -= intercept

	norm2 := norm2(p1x-p2x, p1y-p2y)

	nQueries := nextInt()
	for i := 0; i < nQueries; i++ {
		p3x, _ := nextFloat64()
		p3y, _ := nextFloat64()
		p3y -= intercept

		dot := (p2x-p1x)*p3x + (p2y-p1y)*p3y
		// fmt.Println(dot, norm2)
		fmt.Println(dot*(p2x-p1x)/norm2, dot*(p2y-p1y)/norm2+intercept)
	}
}

