func projection(p1, p2, p []float64) []float64 {
	// p - p1
	v1 := []float64{
		p[0] - p1[0],
		p[1] - p1[1],
	}
	// p2 - p1
	v2 := []float64{
		p2[0] - p1[0],
		p2[1] - p1[1],
	}

	dot := v1[0]*v2[0] + v1[1]*v2[1]
	// cross := v1[0]*v2[1] - v1[1]*v2[0]
	normal := v2[0] * v2[0] + v2[1] * v2[1]

	a := dot / normal

	result := []float64{
		p1[0] + v2[0] * a,
		p1[1] + v2[1] * a,
	}
	return result
}

func reflection(p1, p2, p []float64) []float64 {
	prj := projection(p1, p2, p)
	// p - p1
	v1 := []float64{
		p[0] - p1[0],
		p[1] - p1[1],
	}

	// p to reflection
	toCrossPoint := []float64{
		2 * (prj[0] - v1[0]),
		2 * (prj[1] - v1[1]),
	}

	result := []float64{
		p[0] + toCrossPoint[0],
	    p[1] + toCrossPoint[1],
	}

	return result
}

