const EPS float64 = 1e-8

func add(a float64, b float64) float64 {
	if math.Abs(a+b) < EPS*(math.Abs(a)+math.Abs(b)) {
		return 0.0
	}
	return a + b
}

type Point struct {
	X, Y float64
}

func (p Point) Add(q Point) Point {
	return Point{add(p.X, q.X), add(p.Y, q.Y)}
}

func (p Point) Sub(q Point) Point {
	return Point{add(p.X, -q.X), add(p.Y, -q.Y)}
}

func (p Point) Mul(d float64) Point {
	return Point{p.X * d, p.Y * d}
}

func (p Point) Dot(q Point) float64 {
	return p.X*q.X + p.Y*q.Y
}

type Line struct {
	A, B Point
}

func (l Line) Project(p Point) Point {
	base := l.B.Sub(l.A)
	t := (p.Sub(l.A)).Dot(base) / base.Dot(base)
	return base.Mul(t).Add(l.A)
}

func main() {
	var line Line
	var q int
	var point Point
	fmt.Scan(&line.A.X, &line.A.Y, &line.B.X, &line.B.Y)
	fmt.Scan(&q)
	for i := 0; i < q; i++ {
		fmt.Scan(&point.X, &point.Y)
		result := line.Project(point)
		fmt.Printf("%.8f %.8f\n", result.X, result.Y)
	}
}

