void calc(Vec2 p, Vec2 a, Vec2 b) {
    auto ab = b - a;
    auto ap = p - a;

    auto e = ab.normalized;
    // ベクトル ap のベクトル ab への正射影ベクトル ax を求める
    auto ax = ap.dot(e) * e;
    auto x = a + ax;
    writefln("%.8f %.8f", x.x, x.y);
}

