void calc(Vec2 p, Vec2 a, Vec2 b) {
    auto ab = b - a;
    auto ap = p - a;

    auto d = ab.normalize().dot(ap);
    auto x = a + ab.normalize() * d;
    writefln("%s %s", x.x, x.y);
}

