        solve();
    }
}
fn solve() {
    let (x, y, c, d) = get!(f64, f64, f64, f64);
    let q = get!(usize);

    util::with_bufwriter(|mut out| {
        for _ in 0..q {
            let (a, b) = get!(f64, f64);
            let (a, b) = (a - x, b - y);
            let (c, d) = (c - x, d - y);

            let m = (a * c + b * d) / (c * c + d * d);

            writeln!(out, "{} {}", m * c, m * d).unwrap();
        }
    });
}

