#[derive(Clone, Debug, PartialEq)]
pub struct Line {
    p1: Complex,
    p2: Complex,
}
impl Line {
    pub fn new(p1: Complex, p2: Complex) -> Line {
        Line { p1: p1, p2: p2 }
    }
    pub fn from_param(a: f64, b: f64, x1: f64, x2: f64) -> Line {
        Line {
            p1: Complex::new(x1, a * x1 + b),
            p2: Complex::new(x2, a * x2 + b),
        }
    }
    pub fn projection(&self, p: Complex) -> Complex {
        let e = self.p2 - self.p1;
        let e = e / e.abs();
        let t = (p - self.p1).dot(e);
        self.p1 + e * t
    }
}
fn main() {
    input! { line: [(f64, f64); 2], q, ps: [(f64, f64); q] };
    let line = line
        .into_iter()
        .map(|(x, y)| Complex::new(x, y))
        .collect::<Vec<_>>();
    let ps = ps
        .into_iter()
        .map(|(x, y)| Complex::new(x, y))
        .collect::<Vec<_>>();
    let l = Line::new(line[0], line[1]);
    for i in 0..q {
        let a = l.projection(ps[i]);
        println!("{} {}", a.x, a.y);
    }
}
