    val rot = vx1 * vy2 - vy1 * vx2

    println(if (rot.abs < eps0) {
      if (vx1 * vx2 + vy1 * vy2 < 0) {
        "ONLINE_BACK"
      } else {
        if (vx2*vx2 + vy2*vy2 > vx1*vx1 + vy1*vy1) "ONLINE_FRONT" else "ON_SEGMENT"
      }
