struct Vector {
    f64 x, y;

    Vector() : Vector(0.0,0.0) {}
    Vector(f64 xx, f64 yy) : x(xx), y(yy) {}

    Vector& operator+=(const Vector& rhs) {
        x += rhs.x;
        y += rhs.y;
        return *this;
    }
    Vector& operator-=(const Vector& rhs) {
        x -= rhs.x;
        y -= rhs.y;
        return *this;
    }
    Vector& operator*=(f64 rhs) {
        x *= rhs;
        y *= rhs;
        return *this;
    }
    Vector& operator/=(f64 rhs) {
        x /= rhs;
        y /= rhs;
        return *this;
    }

    f64 norm() const { return x*x + y*y; }
    f64 abs() const { return sqrt(norm()); }
};

const Vector operator+(const Vector& lhs, const Vector& rhs) { return Vector(lhs) += rhs; }
const Vector operator-(const Vector& lhs, const Vector& rhs) { return Vector(lhs) -= rhs; }
const Vector operator*(const Vector& lhs, f64 rhs) { return Vector(lhs) *= rhs; }
const Vector operator*(f64 lhs, const Vector& rhs) { return Vector(rhs) *= lhs; }
const Vector operator/(const Vector& lhs, f64 rhs) { return Vector(lhs) /= rhs; }

bool operator==(const Vector& lhs, const Vector& rhs) {
    return feq(lhs.x,rhs.x) && feq(lhs.y,rhs.y);
}

struct Line {
    Vector p1, p2;

    Line(const Vector& pp1, const Vector& pp2) : p1(pp1), p2(pp2) {}
    Line(f64 x1, f64 y1, f64 x2, f64 y2) : p1(Vector(x1,y1)), p2(Vector(x2,y2)) {}

    Vector vec() const { return p2 - p1; }
};

f64 geo_dot(const Vector& lhs, const Vector& rhs) {
    return lhs.x*rhs.x + lhs.y*rhs.y;
}

f64 geo_cross(const Vector& lhs, const Vector& rhs) {
    return lhs.x*rhs.y - lhs.y*rhs.x;
}

Vector geo_project(const Line& line, const Vector& p) {
    Vector v = line.vec();
    f64 r = geo_dot(p-line.p1, v) / v.norm();
    return line.p1 + r*v;
}

enum class ABC {
    CCW,
    CW,
    ON_BACK,
    ON_FRONT,
    ON_SEGMENT,
};

ABC geo_abc(const Vector& a, const Vector& b, const Vector& c) {
    Vector x = b - a;
    Vector y = c - a;
    f64 cross = geo_cross(x,y);
    if(cross > 0) return ABC::CCW;
    if(cross < 0) return ABC::CW;
    f64 dot = geo_dot(x,y);
    if(dot < 0) return ABC::ON_BACK;
    if(x.norm() < y.norm()) return ABC::ON_FRONT;
    return ABC::ON_SEGMENT;
}

void solve() {
    i64 x0,y0; RD(x0); RD(y0);
    i64 x1,y1; RD(x1); RD(y1);
    Vector p0(x0,y0);
    Vector p1(x1,y1);

    i64 Q; RD(Q);
    REP(_, Q) {
        i64 x2,y2; RD(x2); RD(y2);
        Vector p2(x2,y2);
        ABC ans = geo_abc(p0,p1,p2);
        switch(ans) {
        case ABC::CCW:        PRINTLN("COUNTER_CLOCKWISE"); break;
        case ABC::CW:         PRINTLN("CLOCKWISE");         break;
        case ABC::ON_BACK:    PRINTLN("ONLINE_BACK");       break;
        case ABC::ON_FRONT:   PRINTLN("ONLINE_FRONT");      break;
        case ABC::ON_SEGMENT: PRINTLN("ON_SEGMENT");        break;
        }
    }
}

signed main() {


    solve();

    return 0;
}

