struct intpt_t {
	ll x, y;
	intpt_t operator+(const intpt_t &p) { return { x + p.x, y + p.y }; }
	intpt_t operator-(const intpt_t &p) { return { x - p.x, y - p.y }; }
	intpt_t operator*(const ll &c) { return { x * c, y * c }; }
	intpt_t operator/(const ll &c) { return { x / c, y / c }; }
	bool operator==(const intpt_t &p) { return x == p.x && y == p.y; }
};

struct pt_t {
	double x, y;
	pt_t operator+(const pt_t &p) { return { x + p.x, y + p.y }; }
	pt_t operator-(const pt_t &p) { return { x - p.x, y - p.y }; }
	pt_t operator*(const double &c) { return { x * c, y * c }; }
};

pt_t cvtpt(intpt_t p) {
	return { double(p.x), double(p.y) };
}

// ax + by + c = 0
struct line_t {
	double a, b, c;
};

pt_t cross_point(line_t l, line_t m) {
	pt_t ret = { INFD, INFD };
	double d = l.a * m.b - m.a * l.b;
	if (nearlyeq(d, 0)) return ret;
	double x = l.b * m.c - m.b * l.c;
	double y = l.a * m.c - m.a * l.c;
	ret = { x / d, y / -d };
	return ret;
}

// normal vector = (a, b), passing p
line_t solve_line(double a, double b, pt_t p) {
	return { a, b, -a * p.x - b * p.y };
}

// t should be radius
pt_t rot(pt_t p, double r) {
	return {
		cos(r) * p.x - sin(r) * p.y,
		sin(r) * p.x + cos(r) * p.y
	};
}

double abs(pt_t p) {
	return sqrt(p.x * p.x + p.y * p.y);
}

// angle [0, 2PI) of vector p to vector q
double angle(pt_t p, pt_t q) {
	p = p * (1.0 / abs(p));
	q = q * (1.0 / abs(q));
	double r0 = acos(max(min(p.x * q.x + p.y * q.y, 1.0), -1.0));
	double r1 = asin(max(min(p.x * q.y - p.y * q.x, 1.0), -1.0));
	if (r1 >= 0) return r0;
	else return 2 * M_PI - r0;
}

double dist(line_t l, pt_t p) {
	return abs(l.a * p.x + l.b * p.y + l.c)
		/ sqrt(l.a * l.a + l.b * l.b);
}

bool on_same_line(intpt_t p, intpt_t q) {
	int mask = (p.x ? 0 : 0b1000) + (p.y ? 0 : 0b100)
		+ (q.x ? 0 : 0b10) + (q.y ? 0 : 0b1);
	if ((mask & 0b1100) == 0b1100 || (mask & 0b0011) == 0b0011) return true;
	if ((mask & 0b1010) == 0b1010 || (mask & 0b0101) == 0b0101) return true;
	if (mask == 0b0000) {
		p = p / gcd(p.x, p.y);
		q = q / gcd(q.x, q.y);
		if (p.x < 0) p = p * -1;
		if (q.x < 0) q = q * -1;
		if (p == q) return true;
		else return false;
	}
	else return false;
}

bool on_same_line(pt_t p, pt_t q) {
	line_t l = solve_line(p.y, -p.x, p);
	if (dist(l, q) < eps) return true;
	else return false;
}

int main() {
	intpt_t p0, p1;
	cin >> p0.x >> p0.y >> p1.x >> p1.y;
	int q; cin >> q;
	cout << setprecision(20);
	Loop(i, q) {
		intpt_t p2;
		cin >> p2.x >> p2.y;
		intpt_t ip = p1 - p0;
		intpt_t iq = p2 - p0;
		pt_t p = cvtpt(ip);
		pt_t q = cvtpt(iq);
		bool judge;
		if (on_same_line(p, q)) judge = true;
		else judge = false;
		double r = angle(p, q);
		if (judge) {
			if (M_PI * 0.5 < r && r < M_PI * 1.5) {
				cout << "ONLINE_BACK" << endl;
			}
			else {
				if (abs(q) > abs(p)) {
					cout << "ONLINE_FRONT" << endl;
				}
				else {
					cout << "ON_SEGMENT" << endl;
				}
			}
		}
		else {
			if (r < M_PI) {
				cout << "COUNTER_CLOCKWISE" << endl;
			}
			else {
				cout << "CLOCKWISE" << endl;
			}
		}
	}
}

