// 点aから見た、点bから点cへの方向
int ccw(P a, P b, P c) {
  b -= a; c -= a;
  if (cross(b,c) > 0) return +1;  // counter clockwise
  if (cross(b,c) < 0) return -1;  // clockwise
  if (dot(b,c) < 0) return +2;  // c--a--b on line
  if (norm(b) < norm(c)) return -2; // a--b--c on line or a==b
  return 0;                     // a--c--b on line or a==c or b==c
}

