using namespace std;

static const long double EPS = 1e-10;
static const int COUNTER_CLOCKWISE = 1;
static const int CLOCKWISE = -1;
static const int ONLINE_BACK = 2; // p2→p0→p1
static const int ONLINE_FRONT = -2;
static const int ON_SEGMENT = 0;

class Point{
    public: 
    long double x,y;
    Point(long double x = 0.0, long double y = 0.0): x(x), y(y) {}
    Point operator + (Point p) const { return Point(x+p.x, y+p.y); }
    Point operator - (Point p) const { return Point(x-p.x, y-p.y); }
    Point operator * (long double a) const { return Point(x*a, y*a); }
    Point operator / (long double a) const { return Point(x/a, y/a); }
    bool operator < (const Point &p) const { return (x != p.x ? x < p.x : y < p.y); }
    bool operator == (const Point &p) const { return fabsl(x - p.x) < EPS && fabsl(y - p.y) < EPS; }
    long double abs() const { return sqrt(norm()); }
    long double norm() const { return x*x + y*y; }
    long double dot(Point p) const { return x*p.x + y*p.y; }
    long double cross(Point p) const { return x*p.y - y*p.x; }
};


int ccw(Point p0, Point p1, Point p2){
    Point a = p1 - p0;
    Point b = p2 - p0;
    if(a.cross(b) > EPS) return COUNTER_CLOCKWISE;
    if(a.cross(b) < -EPS) return CLOCKWISE;
    if(a.dot(b) < -EPS) return ONLINE_BACK;
    if(a.dot(b) > EPS) return ONLINE_FRONT;
    return ON_SEGMENT;
}

int main(){
    int x1,y1,x2,y2, n;
    cin >> x1 >> y1 >> x2 >> y2;
    Point p = Point(x1, y1), q = Point(x2, y2);
    cin >> n;
    int x,y;
    for(int i=0;i<n;i++){
        cin >> x >> y;
        switch(ccw(p, q, Point(x, y))){
            case COUNTER_CLOCKWISE:
                cout << "COUNTER_CLOCKWISE";
                break;
            case CLOCKWISE:
                cout << "CLOCKWISE";
                break;
            case ONLINE_BACK:
                cout << "ONLINE_BACK";
                break;
            case ONLINE_FRONT:
                cout << "ONLINE_FRONT";
                break;
            default:
                cout << "ON_SEGMENT";
                break;
        };
        cout << "\n";
    }
}

