using namespace std;

int main() {
	cin.tie(0); ios::sync_with_stdio(false);
	int x0, y0, x1, y1; cin >> x0 >> y0 >> x1 >> y1;
	int q; cin >> q;
	long double theta1, theta2;
	theta1 = atan2(y1 - y0, x1 - x0);
	while (q--) {
		int x2, y2; cin >> x2 >> y2;
		if (x2 == 0 && y2 == 0) { cout << "ON_SEGMENT" << endl; continue; }
		theta2 = atan2(y2 - y0, x2 - x0);
		long double rev_theta = theta1 >= 0 ? theta1 - M_PI : theta1 + M_PI;
		//cout << fixed << setprecision(15) << "theta1 = " << theta1 << endl << "theta2 = " << theta2 << endl << "rev_theta = " << rev_theta << endl;
		if (theta2 - theta1 == M_PI || theta2 - theta1 == -M_PI) { cout << "ONLINE_BACK" << endl; continue; }
		if (theta1 == theta2) {
			long double dist1 = hypot(x1 - x0, y1 - y0), dist2 = hypot(x2 - x0, y2 - y0);
			if (dist2 > dist1) { cout << "ONLINE_FRONT" << endl; continue; }
			else { cout << "ON_SEGMENT" << endl; continue; }
		}
		if (theta1 >= 0) {
			if (theta2 > theta1 || theta2 < rev_theta) { cout << "COUNTER_CLOCKWISE" << endl; continue; }
			if (theta2 < theta1 && theta2 > rev_theta) { cout << "CLOCKWISE" << endl; continue; }
		}
		else {
			if (theta2 > theta1 && theta2 < rev_theta) { cout << "COUNTER_CLOCKWISE" << endl; continue; }
			if (theta2 < theta1 || theta2 > rev_theta) { cout << "CLOCKWISE" << endl; continue; }
		}
	}
}

