#define db double
const db eps = 1e-6;

struct poi {
	db x, y;
	void r() { cin >> x; cin >> y; }
	void w() { cout << x << ' ' << y << '\n'; }
	poi operator -(poi p) {
		return {x - p.x, y - p.y};
	}
	poi operator +(poi p) {
		return {x + p.x, y + p.y};
	}
	poi operator *(db d) {
		return {x * d, y * d};
	}
	db dot(poi p) {
		return x * p.x + y * p.y;
	}
	db cross(poi p) {
		return x * p.y - y * p.x;
	}
	db len2() { return x * x + y * y; }
	poi proj(poi p, poi q) {
		db s = (*this - p).dot(q - p) / (q - p).len2();
		return p + (q - p) * s;
	}
};

int ccw(poi a, poi b, poi c) {
	poi u = b - a, v = c - a;
	if(u.cross(v) > eps) return 1;
	if(u.cross(v) < -eps) return -1;
	if(u.dot(v) < -eps) return -2;
	if(u.len2() + eps < v.len2()) return 2;
	return 0;
}

int main() {
	ios :: sync_with_stdio(false);

	poi p, q; p.r(); q.r();
	int T; cin >> T;
	while(T --) {
		poi t; t.r();
		int a = ccw(p, q, t);
		//cout << a << '\n';
		cout << (a != -2 ? a != -1 ? a != 0 ? a != 1 ?
			"ONLINE_FRONT" :
			"COUNTER_CLOCKWISE" :
			"ON_SEGMENT" :
			"CLOCKWISE" :
			"ONLINE_BACK") << '\n';
	}
	return 0;
}

