string ccw(P a, P b, P c) {
    P ab = b - a, ac = c - a;
    P k = ac * conj(ab);
    if (k.y > 0) return "COUNTER_CLOCKWISE";
    if (k.y < 0) return "CLOCKWISE";
    if (k.x < 0) return "ONLINE_BACK";
    if (abs(ab) < abs(ac)) return "ONLINE_FRONT";
    return "ON_SEGMENT";
}

