#include <bits/stdc++.h>
using namespace std;
struct point {
    double x, y;
    point (){}
    point (double x, double y) : x(x), y(y) {}
    point friend operator - (point l, point r) {
        return point (l.x - r.x, l.y - r.y);
    }
    double friend operator * (point l, point r) {
        return l.x * r.x + l.y * r.y;
    }
    double friend operator ^ (point l, point r) {
        return l.x * r.y - l.y * r.x;
    }
    double len () {
        return sqrt (x * x + y * y);
    }
} p0, p1, p2, P;
int main () {
    int Q;
    cin >> p0.x >> p0.y >> p1.x >> p1.y;
    cin >> Q;
    P = p1 - p0;
    while (Q --) {
        cin >> p2.x >> p2.y;
        p2 = p2 - p0;
        double u = P ^ p2;
        if (u > 0) cout << "COUNTER_CLOCKWISE" << endl;
        else if (u < 0) cout << "CLOCKWISE" << endl;
        else {
            u = P * p2;
            if (u < 0) cout << "ONLINE_BACK" << endl;
            else {
                if (P.len () >= p2.len ()) cout << "ON_SEGMENT" << endl;
                else cout << "ONLINE_FRONT" << endl;
            }
        }
    }
    return 0;
}

