    p1.read(); p2.read();
    int q; scanf("%d",&q);
    while(q--){
        Vec p; p.read();
        if(((p2-p1)^(p-p1))>0)
            printf("COUNTER_CLOCKWISE\n");
        else if(((p2-p1)^(p-p1))<0)
            printf("CLOCKWISE\n");
        else {
            if((p-p1)*(p-p2)<=0)
                printf("ON_SEGMENT\n");
            else if((p-p1).len() < (p-p2).len())
                printf("ONLINE_BACK\n");
            else 
                printf("ONLINE_FRONT\n");
        }
    }
}

