typedef double db;

struct point{
    db x, y;
    point(){}
    point(db a, db b): x(a), y(b){}
    point operator + (const point &p){ return point(x + p.x, y + p.y); }
    point operator - (const point &p){ return point(x - p.x, y - p.y); }
    point operator * (const db &k){ return point(x * k, y * k); }
    point operator / (const db &k){ return point(x / k, y / k); }
    db operator ^ (const point &p){ return x * p.y + y * p.x; }
    db operator * (const point &p){ return x * p.x + y * p.y; }
    bool operator == (const point &p){ return !dcmp(x - p.x) && !dcmp(y - p.y); }
    bool operator != (const point &p){ return dcmp(x - p.x) || dcmp(y - p.y); }
    bool operator < (const point &p){ return x == p.x ? y < p.y : x < p.x; }
    void read(){
        scanf("%lf%lf", &x, &y);
    }
    void print(){
        printf("%.10f %.10f\n", x, y);
    }
    point rotate(db &ang){ return point(x * cos(ang) - y * sin(ang), y * cos(ang) + x * sin(ang)); }
    point norm() { return point(-y, x); }
    db len() { return sqrt(x * x + y * y); }
    db len2(){ return x * x + y * y; }
};

point proj(point A, point B, point P){ // projection point of P in segment AB
    point AB = B - A, AP = P - A;
    return A + AB * (AP * AB / AB.len2());
}

point refl(point A, point B, point P){ // reflection point of P int segment AB
    return proj(A, B, P) * 2 - P;
}

point a, b;
int n;

int main(){
    a.read(), b.read();
    scanf("%d", &n);
    for (int i = 0; i < n; i++){
        point x; x.read();
        int sgn = dcmp((b - a) ^ (x - a));
        if (!sgn){
            if (dcmp((b - a) * (x - a)) < 0){
                puts("ONLINE_BACK");
            }
            else{
                if (dcmp((b - a).len() - (x - a).len()) < 0){
                    puts("ONLINE_FRONT");
                }
                else{
                    puts("ON_SEGMENT");
                }
            }
        }
        else{
            if (sgn > 0){
                puts("COUNTER_CLOCKWISE");
            }
            else{
                puts("CLOCKWISE");
            }
        }
    }
    return 0;
}

#include<bits/stdc++.h>
using namespace std;
typedef complex<double> P;

double eps = 1e-9;

string ccw(P a,P b,P c){
  P ab=b-a;
  P ac=c-a;
  P k=ac*conj(ab);
  if( k.imag() > eps ){
    return "COUNTER_CLOCKWISE";
  }
  if( k.imag() < -eps ){
    return "CLOCKWISE";
  }
  if( k.real() < -eps ){
    return "ONLINE_BACK";
  }  
  if( abs(ab)+eps < abs(ac) ){
    return "ONLINE_FRONT";
  }
  return "ON_SEGMENT";
}

int main(){

  double x0,y0,x1,y1,x2,y2;
  cin>>x0>>y0>>x1>>y1;
  P a(x0,y0);
  P b(x1,y1);
  int n;
  cin>>n;
  while(n--){
    cin>>x2>>y2;
    P c(x2,y2);
    cout<< ccw(a,b,c) <<endl;
  }
  return 0;
}

