class vec2d {
public:
	double x, y;
	vec2d(double x, double y) :x(x), y(y) {}
	double norm() const {
		return sqrt(x * x + y * y);
	}
	vec2d operator+(vec2d v) {
		return vec2d(x + v.x, y + v.y);
	}
	vec2d operator-(vec2d v) {
		return vec2d(x - v.x, y - v.y);
	}
	vec2d operator*(double k) const {
		return vec2d(x * k, y * k);
	}
	vec2d operator/(double k)const {
		return vec2d(x / k, y / k);
	}
	static double inner_product(const vec2d&a, const vec2d&b) {
		return a.x * b.x + a.y * b.y;
	}
	static double cros_product(const vec2d&a, const vec2d&b) {
		return a.x * b.y - a.y * b.x;
	}
	static double argument(const vec2d&a, const vec2d&b) {
		return atan2(cros_product(a, b), inner_product(a, b));
	}
	static vec2d projection(const vec2d&a, const vec2d&b) {
		double n = a.norm();
		return a * vec2d::inner_product(a, b) / n / n;
	}
};
