	Point p1, p2;
	Line() {};
	Line(Point _p1, Point _p2) : p1(_p1), p2(_p2) {};
};

typedef Point Vector;
typedef Line Segment;

Vector operator + (const Vector &a, const Vector &b) {
	return Vector(a.x + b.x, a.y + b.y);
}
Vector operator - (const Vector &a, const Vector &b) {
	return Vector(a.x - b.x, a.y - b.y);
}
Vector operator * (const Vector &a, const db &b) {
	return Vector(a.x * b, a.y * b);
}
Vector operator / (const Vector &a, const db &b) {
	return Vector(a.x / b, a.y / b);
}
db operator * (const Vector &a, const Vector &b) {
	return a.x * b.x + a.y * b.y;
}
db operator ^ (const Vector &a, const Vector &b) {
	return a.x * b.y - a.y * b.x;
}
db dis(const Point &a, const Point &b) {
	return sqrt((a.x - b.x) * (a.x - b.x) + (a.y - b.y) * (a.y - b.y));
}
db dis2(const Point &a, const Point &b) {
	return (a.x - b.x) * (a.x - b.x) + (a.y - b.y) * (a.y - b.y);
}
db dis(const Vector &a) {
	return sqrt(a.x * a.x + a.y * a.y);
}
db dis2(const Vector &a) {
	return a.x * a.x + a.y * a.y;
}
Point Projection(const Line &L, const Point &p) {
	Vector Base = L.p2 - L.p1;
	db len = Base * (p - L.p1);
	return L.p1 + Base * (len / dis2(L.p1, L.p2));
}
Point Reflection(const Line &L, const Point &p) {
	return p + (Projection(L, p) - p) * 2.0;
}

int main() {
	Point p0, p1, p2;
	scanf("%lf%lf%lf%lf", &p0.x, &p0.y, &p1.x, &p1.y);
	int q; scanf("%d", &q);
	while(q --) {
		scanf("%lf%lf", &p2.x, &p2.y);
		Vector p01 = p1 - p0, p02 = p2 - p0;
		if(dcmp(p01 ^ p02) == 1) puts("COUNTER_CLOCKWISE");
		else if(dcmp(p01 ^ p02) == -1) puts("CLOCKWISE");
		else if(dcmp(p01 * p02) == -1) puts("ONLINE_BACK");
		else if(dcmp(dis2(p01), dis2(p02)) == -1) puts("ONLINE_FRONT");
		else puts("ON_SEGMENT");
	}
	return 0;
}

#include<stdio.h>
#include<math.h>

#define EPS (1e-10)
#define equals(a,b) (fabs((a)-(b)) < EPS)

class Point{
public:
  double x,y;

  Point(double x=0,double y=0): x(x),y(y) {}

  Point operator + (Point p) { return Point(x+p.x,y+p.y); }
  Point operator - (Point p) { return Point(x-p.x,y-p.y); }
  Point operator * (double a) { return Point(a*x,a*y); }
  Point operator / (double a) { return Point(a/x,a/y); }

  double abs() { return sqrt(norm()); }
  double norm() { return x*x+y*y; }

  bool operator < (const Point &p) const {
    return x != p.x ? x < p.x : y < p.y;
  }

  bool operator == (const Point &p) const {
    return fabs(x-p.x) < EPS && fabs(y-p.y) < EPS;
  }

};

typedef Point Vector;

double dot(Vector a,Vector b){
  return a.x * b.x + a.y * b.y;
}

double cross(Vector a,Vector b){
  return a.x * b.y - a.y * b.x;
}

int main(){

  double x0,y0,x1,y1,x,y,cr;
  Point p0,p1,p2;
  Vector a,b;
  int q;

  scanf("%lf %lf %lf %lf",&x0,&y0,&x1,&y1);
  p0 = Point(x0,y0);
  p1 = Point(x1,y1);

  scanf("%d",&q);
  for(int i=0;i<q;i++){

    scanf("%lf %lf",&x,&y);
    p2 = Point(x,y);

    a = p1-p0;
    b = p2-p0;

    cr = cross(a,b);

    if(cr > EPS) puts("COUNTER_CLOCKWISE" );
    else if(cr < -EPS) puts("CLOCKWISE");
    else if(dot(a,b) < -EPS) puts("ONLINE_BACK");
    else if(a.abs()<b.abs()) puts("ONLINE_FRONT");
    else puts("ON_SEGMENT");

  }

  return(0);
}

