const double EPS=1e-12;
const double INF=1e9;
const int COUNTER_CLOCKWISE=2;
const int CLOCKWISE=-2;
const int ONLINE_FRONT=1;
const int ONLINE_BACK=-1;
const int ON_SEGMENT=0;


typedef complex<double>Point;
typedef vector<Point>Polygon;
struct Line{
    Point p1,p2;
    Line(const Point &p1,const Point &p2):p1(p1),p2(p2){}
};
struct Segment{
    Point p1,p2;
    Segment(const Point &p1,const Point &p2):p1(p1),p2(p2){}
};
struct Circle{
    Point p;
    int r;
    Circle(Point &p,int r):p(p),r(r){}
};

double dot(Point &p1,Point &p2){
    return real(p1)*real(p2)+imag(p1)+imag(p2);
}

double cross(Point &p1,Point &p2){
    return real(p1)*imag(p2)-real(p2)*imag(p1);
}

int CCW(Point &p1,Point &p2,Point &p3){
    Point a=p2-p1,b=p3-p1;
    if(cross(a,b)>EPS)return COUNTER_CLOCKWISE;
    if(cross(a,b)<-EPS)return CLOCKWISE;
    if(dot(a,b)<-EPS)return ONLINE_BACK;
    if(abs(b)>abs(a)+EPS)return ONLINE_FRONT;
    return ON_SEGMENT;
}

signed main(){
    double x,y;
    scanf("%lf%lf",&x,&y);
    Point p1(x,y);
    scanf("%lf%lf",&x,&y);
    Point p2(x,y);

    int q;scanf("%d",&q);
    while(q--){
        scanf("%lf%lf",&x,&y);
        Point p3(x,y);
        if(CCW(p1,p2,p3)==COUNTER_CLOCKWISE)puts("COUNTER_CLOCKWISE");
        if(CCW(p1,p2,p3)==CLOCKWISE)puts("CLOCKWISE");
        if(CCW(p1,p2,p3)==ONLINE_BACK)puts("ONLINE_BACK");
        if(CCW(p1,p2,p3)==ONLINE_FRONT)puts("ONLINE_FRONT");
        if(CCW(p1,p2,p3)==ON_SEGMENT)puts("ON_SEGMENT");
    }
    return 0;
}

