        const double cross = vec01.cross(vec02);
        if (isEq(cross, 0)) {
            if (vec01.dot(vec02) + EPS < 0)
                puts("ONLINE_BACK");
            else if (vec01.square() + EPS < vec02.square())
                puts("ONLINE_FRONT");
            else puts("ON_SEGMENT");
        } else if (cross < 0)
            puts("CLOCKWISE");
        else puts("COUNTER_CLOCKWISE");
    }

    return 0;
}

