po operator *(const po &a,const db &b){return po(a.x*b,a.y*b);}
po operator /(const po &a,const db &b){return po(a.x/b,a.y/b);}
db operator *(const po &a,const po &b){return a.x*b.x+a.y*b.y;}
db operator ^(const po &a,const po &b){return a.x*b.y-a.y*b.x;}
db mo2(po a){return a*a;}
db mo(po a){return sqrt(a*a);}
po proj(po x,po y){//proj x to y
	return y*((x*y)/mo2(y));
}
po refl(po x,po y){//refl x from y
	return proj(x,y)*2-x;
}
//template
int main(){
	po p1,p2;
	scanf("%lf%lf%lf%lf",&p1.x,&p1.y,&p2.x,&p2.y);
	int t;scanf("%d",&t);
	p2=p2-p1;
	while(t--){
		po x;scanf("%lf%lf",&x.x,&x.y);
		x=x-p1;
		db w=(x^p2);
		if(sign(w)==1){
			puts("CLOCKWISE");
			continue;
		}
		if(sign(w)==-1){
			puts("COUNTER_CLOCKWISE");
			continue;
		}
		if(sign(x*p2)==-1){
			puts("ONLINE_BACK");
			continue;
		}
		if(cmp(mo2(x),mo2(p2))<=0){
			puts("ON_SEGMENT");
			continue;
		}
		puts("ONLINE_FRONT");
		continue;
	}
	return 0;
}

