{
  return p + (project(l, p) - p) * 2.0;
}

bool isOrthogonal(Line l1, Line l2)
{
  return EQ(dotproduct(l1.p1 - l1.p2, l2.p1 - l2.p2), 0.0);
}

bool isParallel(Line l1, Line l2)
{
  return EQ(crossproduct(l1.p1 - l1.p2, l2.p1 - l2.p2), 0.0);
}

//-------Counter-Clockwise---------
const int COUNTER_CLOCKWISE = +1;
const int CLOCKWISE = -1;
const int ONLINE_BACK = +2;
const int ONLINE_FRONT = -2;
const int ON_SEGMENT = 0;

int ccw(Point a, Point b, Point c){
  Point x = b - a;
  Point y = c - a;
  if(crossproduct(x, y) > EPS)
    return COUNTER_CLOCKWISE;
  if(crossproduct(x, y) < -EPS)
    return CLOCKWISE;
  if(dotproduct(x, y) < -EPS)
    return ONLINE_BACK;
  if(norm(x) < norm(y))
    return ONLINE_FRONT;
  return ON_SEGMENT;
}

int main()
{
  Point a, b, c;
  cin >> a.X >> a.Y >> b.X >> b.Y;
  int n;
  cin >> n;
  for(int i = 0; i < n; i++){
    cin >> c.X >> c.Y;
    int x = ccw(a, b, c);
    if(x == COUNTER_CLOCKWISE)
      cout << "COUNTER_CLOCKWISE" << endl;
    else if(x == CLOCKWISE)
      cout << "CLOCKWISE" << endl;
    else if(x == ONLINE_BACK)
      cout << "ONLINE_BACK" << endl;
    else if(x == ONLINE_FRONT)
      cout << "ONLINE_FRONT" << endl;
    else
      cout << "ON_SEGMENT" << endl;
  }
}

