	scanf("%d", &q);

	for (int i = 0; i < q; i++)
	{
		scanf("%lf %lf", &p2x, &p2y);

		if ((p2x == p0x && p2y == p0y) || (p2x == p1x && p2y == p0y)){
			printf("ONLINE_SEGMENT\n");

			continue;
		}

		pair<double, double> v02 = { p2x - p0x, p2y - p0x };

		if (v01.first*v02.second == v01.second*v02.first)
		{
			if (v01.first*v02.first < 0)
