// 浮動小数点のゼロ判定
#define EPS (1e-10)
#define equals(a, b) ( fabs((a) - (b)) < EPS )

// 点を表すクラス
class Point
{
    public:
        double x, y;
        // コンストラクタ
        Point(double x = 0.0, double y = 0.0) : x(x), y(y){ }

        // -----▼▼▼ 演算子のオーバーロード ▼▼▼-----

        Point operator + (Point &p)
        {
            return Point(x + p.x, y + p.y);
        }

        Point operator - (Point &p)
        {
            return Point(x - p.x, y - p.y);
        }

        Point operator * (double a)
        {
            return Point(a * x, a * y);
        }

        Point operator / (double a)
        {
            return Point(x / a, y / a);
        }

        bool operator < ( const Point &p) const
        {
            return x != p.x ? x < p.x : y < p.y;
        }

        bool operator == ( const Point &p) const
        {
            return fabs(x - p.x) < EPS && fabs(y -p.y) < EPS;
        }

        // -----▲▲▲ 演算子のオーバーロード ▲▲▲-----

        // 22点間の距離の算出
        double distance()
        {
            return sqrt( norm() );
        }

        double norm()
        {
            return ( x * x ) + ( y * y );
        }
};

// 線分を表すクラス
class Segment
{
    public:
        Point p1, p2;
        // コンストラクタ
        Segment(Point p1, Point p2) : p1(p1), p2(p2){ }
};

// 点とベクトルの表現は同じ
typedef Point Vector;
// 線分と直線の表現は同じ
typedef Segment Line;

double dot(Vector a, Vector b);
double cross(Vector a, Vector b);
double cross_point(Line line, Point pp);
Vector projection(Vector a, Vector b, Vector p);
double getDistanceLP(Line l, Point p);

int main(void)
{
    int q;
    double dot_val, cross_val, cos_theta;
    double p0_p1_distance, p0_p2_distance;
    Point p0, p1, p2;

    // p0, p1の格納
    cin >> p0.x >> p0.y >> p1.x >> p1.y;

    cin >> q;

    for(int i = 0; i < q; i++)
    {
        // p2の格納
        cin >> p2.x >> p2.y;

        // ベクトルの距離の算出
        p0_p1_distance = (p1 - p0).distance();
        p0_p2_distance = (p2 - p0).distance();

        // [p0p1ベクトル]と, [p0p2ベクトル]の内積を求める
        dot_val = dot(p1 - p0, p2 - p0);
        // コサインの値を求める
        cos_theta = dot_val / ( p0_p1_distance * p0_p2_distance );

        // [p0p1ベクトル]と, [p0p2ベクトル]の外積を求める
        cross_val = cross(p1 - p0, p2 - p0);

        // 判定処理
        if( cross_val > 0.0 )
        {
            // p2が反時計回りの方向にある時
            printf("COUNTER_CLOCKWISE\n");
        }
        else if( cross_val < 0.0 )
        {
            // p2が時計回りの方向にある時
            printf("CLOCKWISE\n");
        }
        else if( equals(cos_theta, -1.0) )
        {
            // なす角が180°の時
            printf("ONLINE_BACK\n");
        }
        else
        {
            // なす角が0°の時
            if( p0_p1_distance < p0_p2_distance )
            {
                // p0, p1, p2 がこの順で同一直線上にある場合
                printf("ONLINE_FRONT\n");
            }
            else
            {
                // p2 が線分 p0p1 上にある場合
                // [p0とp2], [p1とp2] が同一点である時も含む
                printf("ON_SEGMENT\n");
            }
        }
    }

    return 0;
}

// 内積の計算をする関数
double dot(Vector a, Vector b)
{
    return ( ( a.x * b.x ) + ( a.y * b.y ) );
}

// 外積の計算をする関数
double cross(Vector a, Vector b)
{
    return ( ( a.x * b.y ) - ( a.y * b.x ) );
}

// 外積の計算をする関数 2
// 直線 : line に対する点pの外積の演算
double cross_point(Line line, Point pp)
{
    return ( line.p2.x - line.p1.x ) * ( pp.y - line.p1.y ) - ( pp.x - line.p1.x ) * ( line.p2.y - line.p1.y );
}

// 正射影ベクトルを求める関数
Vector projection(Vector a, Vector b, Vector p)
{
    // 正射影ベクトルの考え方より
    // 正射影ベクトル = ( ( a, bベクトルの内積 ) / aベクトルの大きさの2乗 ) * aベクトル
    return ( ( a * ( dot(a, b) / pow( a.distance(), 2.0 ) ) ) + p );
}

// 点と直線の距離を求める関数
double getDistanceLP(Line l, Point p)
{
    if( dot( l.p2 - l.p1, p - l.p1 ) < 0.0 )
    {
        // 内積の値が負の場合, 2つの線分のなす角が90°以上のため,
        // 距離は, p と l.p1 の距離そのものになる
        return ( p - l.p1 ).distance();
    }

    if( dot( l.p1 - l.p2, p - l.p2 ) < 0.0 )
    {
        // 内積の値が負の場合, 2つの線分のなす角が90°以上のため,
        // 距離は, p と l.p2 の距離そのものになる
        return ( p - l.p2 ).distance();
    }

    // 2つの線分のなす角が90°以内の時
    return abs( cross(l.p2 - l.p1, p - l.p1) / ( l.p2 - l.p1 ).distance() );
}

