int gaiseki(int ax, int ay, int bx, int by){
  return ax*by - ay*bx;
}

int naiseki(int ax, int ay, int bx, int by){
  return ax*bx + ay*by;
}

double norm(int ax, int ay){
  return sqrt(ax*ax+ay*ay);
}

